import 'package:app/src/home/presentation/pages/home_page.dart';import 'package:app/src/main_index.dart';import 'package:app/src/request_log/presentation/add_request/pages/add_request_page.dart';import 'package:app/src/request_log/presentation/view/pages/request_log_page.dart';import '../../../attendance_record/presentation/pages/attendance_record_page.dart';import '../../../notifications/presentation/pages/notifications_page.dart';import '../../../profile/presentation/pages/profile_page.dart';import '../bloc/nav_bar_bloc.dart';class NavigationBarPage extends BaseStatelessWidget {  NavigationBarPage({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    List<NavigatorModel> items = [      NavigatorModel(        title: strings.home,        screen: AddRequestPage(),        icon: AppIcons.home,      ),      NavigatorModel(        title: strings.request_log,        screen: RequestLogPage(),        icon: AppIcons.activity,      ),      NavigatorModel(        title: strings.attendance_record,        screen: AttendanceRecordPage(),        icon: AppIcons.calendar,      ),      NavigatorModel(        title: strings.profile,        screen: ProfilePage(),        icon: AppIcons.calendar,      ),    ];    return BlocProvider(      create: (context) => NavBarBloc(),      child: BlocBuilder<NavBarBloc, NavBarState>(        builder: (context, state) {          NavBarBloc bloc = NavBarBloc.get(context);          return Scaffold(            bottomNavigationBar: Container(              margin: 10.paddingAll,              clipBehavior: Clip.antiAlias,              decoration: Decorations.baseDecorationRadius(),              child: BottomNavigationBar(                onTap: (index) {                  bloc.changeIndex(index);                },                currentIndex: bloc.currentIndex,                type: BottomNavigationBarType.fixed,                showSelectedLabels: true,                selectedLabelStyle: whiteMediumStyle.copyWith(fontSize: 12),                unselectedLabelStyle: hintMediumStyle.copyWith(fontSize: 12,),                elevation: 10,                selectedIconTheme: IconThemeData(                  color: whiteMediumStyle.color,                ),                selectedItemColor: whiteMediumStyle.color,                unselectedItemColor: hintColor,                backgroundColor: primaryColor,                items: items                    .map((e) => BottomNavigationBarItem(                  icon: AppIcon(                    icon: e.icon,                    size: 20,                    padding: 3.paddingBottom,                  ),                  activeIcon: AppIcon(                    icon: e.icon,                    color: whiteMediumStyle.color,                    padding: 3.paddingBottom,                    size: 22,                  ),                  label: e.title,                ))                    .toList(),              ),            ),            body: items[bloc.currentIndex].screen,          );        },      ),    );  }}class NavigatorModel {  final String title;  final Widget screen;  final String icon;  NavigatorModel({    required this.title,    required this.screen,    required this.icon,  });}