import 'package:app/src/home/presentation/pages/home/home_page.dart';import 'package:app/src/qayds/presentation/register_qayd/pages/register_qayd_page.dart';import 'package:flutter/material.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:flutter_svg/svg.dart';import '../../../../core/assets/app_icons.dart';import '../../../../core/components/base_stateless_widget.dart';import '../../../invoices/presentation/invoices/pages/invoices_page.dart';import '../../../profile/presentation/pages/profile_page.dart';import '../bloc/nav_bar_bloc.dart';class NavigationBarPage extends BaseStatelessWidget {  NavigationBarPage({    Key? key,  }) : super(key: key);  @override  Widget build(BuildContext context) {    List<NavigatorModel> items = [      NavigatorModel(          title: strings.home,          screen: HomePage(),          icon: AppIcons.home,      ),      NavigatorModel(        title: strings.invoices,        screen: InvoicesPage(),        icon: AppIcons.invoices,      ),      NavigatorModel(        title: strings.qayd,        screen: RegisterQaydPage(),        icon: AppIcons.qayd,      ),      NavigatorModel(        title: strings.account,        screen: ProfilePage(),        icon: AppIcons.account,      ),    ];    return BlocProvider(      create: (context) => NavBarBloc(),      child: BlocBuilder<NavBarBloc, NavBarState>(        builder: (context, state) {          NavBarBloc bloc = NavBarBloc.get(context);          return Scaffold(            bottomNavigationBar: BottomNavigationBar(                  onTap: (index) {                    bloc.changeIndex(index);                  },                  currentIndex: bloc.currentIndex,                  type: BottomNavigationBarType.fixed,                  showSelectedLabels: true,                  selectedLabelStyle: headlineMedium.copyWith(fontSize: 14),                  unselectedLabelStyle: bodyMedium.copyWith( fontSize: 14),                  elevation: 10,              selectedItemColor: primaryColor,              unselectedItemColor: primaryColorDark,                  backgroundColor: scaffoldBackgroundColor,                  items: items                      .map((e) => BottomNavigationBarItem(                            icon: SvgPicture.asset(                              e.icon,                            ),                            activeIcon: SvgPicture.asset(e.icon,                                color: const Color(0xffF1CB06)),                            label: e.title,                          ))                      .toList(),                ),            body: items[bloc.currentIndex].screen,          );        },      ),    );  }}class NavigatorModel {  final String title;  final Widget screen;  final String icon;  NavigatorModel({    required this.title,    required this.screen,    required this.icon,  });}