import 'package:app/src/home/presentation/pages/home_page.dart';import 'package:app/src/main_index.dart';import 'package:app/src/product/presentation/pages/view/product_page.dart';import '../../../more/presentation/pages/more_page.dart';import '../../../requests/presentation/view/pages/requests_page.dart';import '../bloc/nav_bar_bloc.dart';class NavigationBarPage extends BaseStatelessWidget {  NavigationBarPage({    Key? key,  }) : super(key: key);  final GlobalKey<ScaffoldState> _scaffoldKey = GlobalKey<ScaffoldState>();  @override  Widget build(BuildContext context) {    List<NavigatorModel> items = [      NavigatorModel(        title: strings.home,        screen: HomePage(),        icon: AppIcons.home,      ),      NavigatorModel(        title: strings.products,        screen: ProductPage(),        icon: AppIcons.category,      ),      NavigatorModel(        title: strings.request_log,        screen: RequestsPage(),        icon: AppIcons.reservation,      ),      NavigatorModel(        title: strings.more,        screen: MorePage(),        icon: AppIcons.more,      ),    ];    return BlocProvider(      create: (context) => NavBarBloc(),      child: BlocBuilder<NavBarBloc, NavBarState>(        builder: (context, state) {          NavBarBloc bloc = NavBarBloc.get(context);          return Scaffold(            key: _scaffoldKey,            bottomNavigationBar: Container(              clipBehavior: Clip.antiAlias,              decoration: Decorations.baseDecorationRadius(),              child: BottomNavigationBar(                onTap: (index) {                  bloc.changeIndex(index);                },                currentIndex: bloc.currentIndex,                type: BottomNavigationBarType.fixed,                showSelectedLabels: true,                selectedLabelStyle: whiteBoldStyle.copyWith(fontSize: 12),                unselectedLabelStyle: whiteBoldStyle.copyWith(                  fontSize: 12,                ),                elevation: 10,                selectedIconTheme: IconThemeData(                  color: whiteMediumStyle.color,                ),                selectedItemColor: primaryColor,                unselectedItemColor: Colors.grey,                backgroundColor: whiteTextColor,                items: items                    .map((e) => BottomNavigationBarItem(                          icon: AppIcon(                            icon: e.icon,                            size: 25,                            padding: 3.paddingBottom,                            color: Colors.grey,                          ),                          activeIcon: AppIcon(                            icon: e.icon,                            color: primaryColor,                            padding: 3.paddingBottom,                            size: 25,                          ),                          label: e.title,                        ))                    .toList(),              ),            ),            body: items[bloc.currentIndex].screen,          );        },      ),    );  }}class NavigatorModel {  final String title;  final Widget screen;  final String icon;  NavigatorModel({    required this.title,    required this.screen,    required this.icon,  });}